import React from 'react';
import PropTypes from 'prop-types';
import './Filters.scss';


const Filters = props => {
    const {getQuery, handleChecked} = props;
    return (
        <React.Fragment>
            <label className="label" htmlFor="search">Busca tu personaje</label>
            <input className="input" type="text" name="search" onChange={getQuery}/>
            <div>
            <label htmlFor="all">
              <input
                id="all"
                type="checkbox"
                value="all"
                name="species"
                onChange= {handleChecked}
                checked={this.state.specie === ''}
              />
              All
            </label>
          </div>
          <div>
            <label htmlFor="alien">
              <input
                id="alien"
                type="checkbox"
                value="Alien"
                name="species"
                onChange= {handleChecked}
                checked={this.state.specie === 'Alien'}
              />
              Alien
              </label>
          </div>
          <div>
            <label htmlFor="human">
              <input
                id="human"
                type="checkbox"
                value="Human"
                name="species"
                onChange= {handleChecked}
                checked={this.state.specie === 'Human'}
              />
              Human
            </label>
          </div>
        </React.Fragment>
    );
};

Filters.propTypes = {
    getQuery: PropTypes.func.isRequired
}
export default Filters;
