import React from 'react';
import './App.css';
import {fetchData} from './services/get-data';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [],
      query: ''
    }
  }

  componentDidMount () {
    this.getData()
  }

  getData () {
    fetchData()
    .then(data => {
      this.setState({
        data: data.results
      })
    })
  }

  getQuery(event) {
    const value = event.currentTarget.value;
    console.log(value)
  }

  render() {
    return (
      <div className="app">
        <header>
          <h1 className="app__title">Rick y Morty Wiki</h1>
        </header>
        <main>
          <label htmlFor="search">Busca tu personaje</label>
          <input type="text" />
          <ul className="app__list">
            {this.state.data.map(charac => {
              return (
                <li className="character" >
                  <h2 className="name__card">{charac.name}</h2>
                  <div className="img__container">
                    <img src={charac.image} alt={charac.name} className="img"/>
                  </div>
                  <p className="specie">{charac.species}</p>
                </li>
              );
            })}
          </ul>
        </main>
      </div>
    );
  }
}

export default App;
