import React from 'react';
import './App.scss';
import { fetchData } from './services/get-data';
import Header from './components/Header';
import Home from './components/Home';
import CharacterDetail from './components/CharacterDetail';
import { Switch, Route } from 'react-router-dom';


class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [],
      query: '',
      checked:''
    }
    this.getQuery = this.getQuery.bind(this);
    this.handleChecked=this.handleChecked.bind(this);
  }

  componentDidMount() {
    this.getData()
  }

  getData() {
    fetchData()
      .then(data => {
        this.setState({
          data: data.results
        })
      })
  }

  getQuery(event) {
    const value = event.currentTarget.value;
    this.setState({
      query: value
    })
  }

  handleChecked (event) {
    const isChecked = event.currentTarget.id;
    console.log(isChecked)
    if(isChecked === 'All') {
      return (this.setState({
        checked: isChecked
      }));
    } else if(isChecked === 'Alien') {
        return (this.setState({
          checked: isChecked
        }));
    } else {
      return (this.setState({
        checked: isChecked
      }));
    }

  }
  render() {
    return (
      <div className="app">
        <Header />
        <main>
          <div>
            <label htmlFor="all">
              <input
                id="all"
                type="checkbox"
                value="all"
                name="species"
                onChange= {this.handleChecked}
              />
              All
            </label>
          </div>
          <div>
            <label htmlFor="alien">
              <input
                id="alien"
                type="checkbox"
                value="alien"
                name="species"
                onChange= {this.handleChecked}
              />
              Alien
              </label>
          </div>
          <div>
            <label htmlFor="human">
              <input
                id="human"
                type="checkbox"
                value="human"
                name="species"
                onChange= {this.handleChecked}
              />
              Human
            </label>
          </div>
          <Switch>
            <Route exact path="/" render={() => {
              return (
                <Home
                  getQuery={this.getQuery}
                  data={this.state.data}
                  query={this.state.query} />
              );
            }} />
            <Route path="/character/:id" render={(routerProps) => {
              return (
                <CharacterDetail
                  data={this.state.data}
                  routerProps={routerProps} />
              );
            }} />
          </Switch>


        </main>
      </div>
    );
  }
}

export default App;
