import React from 'react';
import './App.css';
import {fetchData} from './services/get-data';
import Filters from './components/Filters';
import CharacterList from './components/CharacterList';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [],
      query: ''
    }
    this.getQuery= this.getQuery.bind(this);
  }

  componentDidMount () {
    this.getData()
  }

  getData () {
    fetchData()
    .then(data => {
      this.setState({
        data: data.results
      })
    })
  }

  getQuery (event) {
    const value = event.currentTarget.value;
    this.setState({
      query: value
    })
  }


  render() {
    return (
      <div className="app">
        <header>
          <h1 className="app__title">Rick y Morty Wiki</h1>
        </header>
        <main>
          <Filters getQuery={this.getQuery}/>
          <CharacterList data={this.state.data}/>

        </main>
      </div>
    );
  }
}

export default App;
