import React from 'react';
import PropTypes from 'prop-types';
import {Link} from 'react-router-dom';

const CharacterDetail = props => {
    const {data, routerProps}= props;
    const newId = parseInt(routerProps.match.params.id);
    const character = data.filter(item => item.id === newId);

    if (character[0]) {
        const {name, image, species}= character[0];
        return (
            <div className="detail__container">
            <h2 className="name__detail">{name}</h2>
            <div className="img__container">
                <img src={image} alt={name} className="img"/>
            </div>
            <p className="specie">{species}</p>
            <Link to="/">Volver a los personajes</Link>
        </div>
        );
    } else {
        return (
            <React.Fragment>
                <p>No hay m√°s personajes</p>
                <Link to="/">Volver a los personajes</Link>
            </React.Fragment>
            );
    }

};


export default CharacterDetail;
