import React from 'react';
import './App.scss';
import {fetchData} from './services/get-data';
import Header from './components/Header';
import Home from './components/Home';
import CharacterDetail from './components/CharacterDetail';
import {Switch, Route} from 'react-router-dom';


class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [],
      query: ''
    }
    this.getQuery= this.getQuery.bind(this);
  }

  componentDidMount () {
    this.getData()
  }

  getData () {
    fetchData()
    .then(data => {
      this.setState({
        data: data.results
      })
    })
  }

  getQuery (event) {
    const value = event.currentTarget.value;
    this.setState({
      query: value
    })
  }


  render() {
    return (
      <div className="app">
        <Header />
        <main>
        <h3>Suplementos del vuelo:</h3>
<div>
  <label htmlFor="human">
    <input
      id="human"
      type="checkbox"
      value="human"
      name="species"
    />
    Selecci√≥n de asiento
  </label>
</div>
<div>
  <label for="alien">
    <input
      id="alien"
      type="checkbox"
      value="alien"
      name="species"
    />
    Seguro
  </label>
</div>
<div>
  <label for="all">
    <input
      id="all"
      type="checkbox"
      value="all"
      name="species"
    />
    Coche de alquiler
  </label>
</div>
          <Switch>
            <Route exact path="/" render = {() => {
              return (
                <Home
                getQuery={this.getQuery}
                data={this.state.data}
                query={this.state.query}/>
              );
            }}/>
            <Route path= "/character/:id" render = {(routerProps)=> {
              return (
                <CharacterDetail 
                data={this.state.data}
                routerProps={routerProps}/>
              );
            }}/>
          </Switch>


        </main>
      </div>
    );
  }
}

export default App;
