import React from 'react';


const CharacterList = props => {
    const {data, query}= props;
    return (
        <ul className="app__list">
        {data
        .filter(myCharac => myCharac.name.toUpperCase().includes(query.toUpperCase()))
        .map(charac => {
          return (
            <li className="character" key={charac.id}>
                <div className="card">
                    <h2 className="name__card">{charac.name}</h2>
                    <div className="img__container">
                        <img src={charac.image} alt={charac.name} className="img"/>
                    </div>
                    <p className="specie">{charac.species}</p>
                </div>
            </li>
          );
        })}
      </ul>
    );
};


export default CharacterList;
