import React from 'react';
import './App.css';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      data: []
    }
  }

  componentDidMount () {
    this.getData()
  }

  getData () {
    const ENDPOINT = 'https://rickandmortyapi.com/api/character/';

    fetch (ENDPOINT)
    .then(res=>res.json())
    .then(data => {
      console.log(data.results)
    })
  }

  render() {
    return (
      <div className="App">

      </div>
    );
  }
}

export default App;
