import React from 'react';
import './App.css';
import {fetchData} from './services/get-data';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      data: []
    }
  }

  componentDidMount () {
    this.getData()
  }

  getData () {
    fetchData()
    .then(data => {
      this.setState({
        data: data.results
      })
    })
  }

  render() {
    return (
      <div className="app">
        <ul className="app__list">
          {this.state.data.results.map(charac => {
            return (
              <li className="character">
                <div className="card">
                  <h2 className="name__card">{charac.results.name}</h2>
                </div>
              </li>
            );
          })}
        </ul>
      </div>
    );
  }
}

export default App;
