import React from 'react';
import './App.css';
import {fetchData} from './services/get-data';
import Filters from './components/Filters';
import CharacterList from './components/CharacterList';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [],
      query: ''
    }
    this.getQuery= this.getQuery.bind(this);
  }

  componentDidMount () {
    this.getData()
  }

  getData () {
    fetchData()
    .then(data => {
      this.setState({
        data: data.results
      })
    })
  }

  getQuery (event) {
    const value = event.currentTarget.value;
    this.setState({
      query: value
    })
  }


  render() {
    return (
      <div className="app">
        <header>
          <h1 className="app__title">Rick y Morty Wiki</h1>
        </header>
        <main>
          <Filters getQuery={this.getQuery}/>
          <ul className="app__list">
            {this.state.data
            .filter(myCharac => myCharac.name.toUpperCase().includes(this.state.query.toUpperCase()))
            .map(charac => {
              return (
                <li className="character" key={charac.id}>
                  <h2 className="name__card">{charac.name}</h2>
                  <div className="img__container">
                    <img src={charac.image} alt={charac.name} className="img"/>
                  </div>
                  <p className="specie">{charac.species}</p>
                </li>
              );
            })}
          </ul>
        </main>
      </div>
    );
  }
}

export default App;
